CREATE TABLE `exam_order` (
  `id` bigint(19) NOT NULL AUTO_INCREMENT COMMENT '自增id',
  `order_no` varchar(20) CHARACTER SET utf8mb4 DEFAULT '' COMMENT '订单号',
  `course_id` bigint(20) DEFAULT NULL COMMENT '课程id',
  `course_title` varchar(100) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '课程名称',
  `course_cover` varchar(255) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '课程封面',
  `teacher_name` varchar(20) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '讲师名称',
  `user_id` bigint(20) DEFAULT NULL COMMENT '用户id',
  `nickname` varchar(50) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '用户昵称',
  `mobile` varchar(11) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '用户手机',
  `account` varchar(100) DEFAULT NULL COMMENT '用户账号',
  `total_fee` decimal(10,2) DEFAULT '0.01' COMMENT '订单金额（分）',
  `status` tinyint(3) DEFAULT NULL COMMENT '订单状态（0：待支付  1:已支付 2：已取消）',
  `is_deleted` tinyint(1) unsigned DEFAULT '0' COMMENT '逻辑删除 1（true）已删除， 0（false）未删除',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `modified_time` datetime DEFAULT NULL COMMENT '更新时间',
