/*Table structure for table `exam_paper` */

DROP TABLE IF EXISTS `exam_paper`;

CREATE TABLE `exam_paper` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '试卷id',
  `name` varchar(255) DEFAULT NULL COMMENT '试卷名称',
  `course_id` bigint(11) DEFAULT NULL COMMENT '考试科目',
  `question_count` int(11) DEFAULT NULL COMMENT '题目数量',
  `score` int(11) DEFAULT NULL COMMENT '试卷总分',
  `suggest_time` int(11) DEFAULT NULL COMMENT '建议时间（分钟）',
  `time` varchar(255) DEFAULT NULL COMMENT '考试时间',
  `user` varchar(200) DEFAULT NULL COMMENT '创建人',
  `status` tinyint(11) DEFAULT NULL COMMENT '试卷开放状态 0:不开放  1:开放',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `modified_time` datetime DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `course_id` (`course_id`),
  CONSTRAINT `exam_paper_ibfk_1` FOREIGN KEY (`course_id`) REFERENCES `exam_course` (`id`) ON DELETE SET NULL ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=1426517788 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

